// Front Page - Divisions Slider Functions
function DivisionsSlider() {
  var divisions_slide_num = $('#divisionsSliderSection__divisions .carousel-item').length;
  $('#divisionsSliderSection__divisions .num').html('1/' + divisions_slide_num);

  $('#divisionsSliderSection__divisions').bind('slid.bs.carousel', function () {
    var current_divisions_slide = $('#divisionsSliderSection__divisions .carousel-item.active').index() + 1;
    $('#divisionsSliderSection__divisions .num').html(current_divisions_slide + '/' + divisions_slide_num);
  });
}

